import React, { useState } from "react";
import { VictoryArea, VictoryChart, VictoryAxis, VictoryVoronoiContainer, VictoryTooltip } from 'victory';
import "./achievements.css";

const Achievements = () => {
    const allData = [
        { 
            year: 2020, 
            Ciencias: { 
                Olcoquim: { gold: 2, silver: 1, bronze: 0, mention: 0 },
                Olcofi: { gold: 1, silver: 0, bronze: 1, mention: 1 },
                Olicocibi: { gold: 0, silver: 1, bronze: 1, mention: 0 },
                Astronomía: { gold: 0, silver: 0, bronze: 0, mention: 1 },
            },
            Tecnología: { 
                Robótica: { gold: 3, silver: 2, bronze: 1, mention: 0 },
                Informática: { gold: 2, silver: 1, bronze: 1, mention: 1 },
            },
            Arte: {
            },
            Graduados: {
                hombres: 20,
                mujeres: 8
            },
            Otros: {
            }
        },
    ];
    

    const [selectedCategory, setSelectedCategory] = useState('Ciencias');
    const [selectedSubCategory, setSelectedSubCategory] = useState(null); // Estado para las subcategorías

    // Opciones de categorías y subcategorías
    const categories = ["Ciencias", "Tecnología", "Graduados", "Otros"];
    
    const subCategories = selectedCategory !== 'Graduados' 
        ? Object.keys(allData[0][selectedCategory] || {}) 
        : ['hombres', 'mujeres'];

    
    // Filtrado de datos basado en la categoría y subcategoría seleccionada
    const filteredData = allData.map(item => {
        const categoryData = item[selectedCategory];
        if (selectedCategory === 'Graduados') {
            return { year: item.year, count: categoryData[selectedSubCategory] || 0 };
        } else if (categoryData && selectedSubCategory && categoryData[selectedSubCategory]) {
            const subCategoryData = categoryData[selectedSubCategory];
            return { year: item.year, count: subCategoryData.gold + subCategoryData.silver + subCategoryData.bronze + subCategoryData.mention };
        }
        return { year: item.year, count: 0 };
    });

    return (
        <div className="achievements-container">
            <div className="achievements-banner">
                <h1>Logros y reconocimientos</h1>
                <p>Descubre nuestras destacadas contribuciones y logros.</p>
            </div>
    
            <div className="selection-controls">
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))}
                </select>
    
                {/* La selección de subcategorías solo aparece si la categoría seleccionada no es 'Graduados' */}
                {selectedCategory !== 'Graduados' && (
                    <select onChange={(e) => setSelectedSubCategory(e.target.value)} value={selectedSubCategory || ''}>
                        {subCategories.map(subCategory => (
                            <option key={subCategory} value={subCategory}>{subCategory}</option>
                        ))}
                    </select>
                )}
            </div>
    
            <div className="chart-container">
                <VictoryChart
                    domainPadding={20}
                    containerComponent={
                        <VictoryVoronoiContainer
                            voronoiDimension="x"
                            labels={({ datum }) => `Año: ${datum.year}\n${selectedSubCategory}: ${datum[selectedSubCategory]}`}
                            labelComponent={
                                <VictoryTooltip
                                    cornerRadius={0}
                                    flyoutStyle={{ fill: "white" }}
                                />
                            }
                        />
                    }
                >
                    <VictoryAxis />
                    <VictoryAxis dependentAxis />
                    <VictoryArea
                        data={filteredData}
                        x="year"
                        y={(datum) => datum[selectedSubCategory]}
                        interpolation="natural"
                    />
                </VictoryChart>
            </div>
        </div>
    );
    
                       
}

export default Achievements;
