import React, { useState } from "react";
import { VictoryArea, VictoryChart, VictoryAxis, VictoryVoronoiContainer, VictoryTooltip } from 'victory';
import "./achievements.css";

const Achievements = () => {
    const allData = [
        { 
            year: 2020, 
            Ciencias: { 
                Olcoquim: { gold: 2, silver: 1, bronze: 0, mention: 0 },
                Olcofi: { gold: 1, silver: 0, bronze: 1, mention: 1 },
                Olicocibi: { gold: 0, silver: 1, bronze: 1, mention: 0 },
                Astronomía: { gold: 0, silver: 0, bronze: 0, mention: 1 },
            },
            Tecnología: { 
                Robótica: { gold: 3, silver: 2, bronze: 1, mention: 0 },
                Informática: { gold: 2, silver: 1, bronze: 1, mention: 1 },
            },
            Arte: {
            },
            Graduados: {
                hombres: 50,
                mujeres: 60
            },
            Otros: {
                // ... Otros subcategorías y premios
            }
        },
        // ... datos para otros años ...
    ];
    
    const [selectedCategory, setSelectedCategory] = useState('Ciencias');
    const categories = ["Ciencias", "Tecnología", "Graduados", "Otros", "Global"];

    const filteredData = allData.filter(item => item.category === selectedCategory);

    return (
        <div className="achievements-container">
            <div className="achievements-banner">
                <h1>Logros y reconocimientos</h1>
                <p>Descubre nuestras destacadas contribuciones y logros.</p>
            </div>

            <div className="selection-controls">
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))}
                </select>
            </div>

            <div className="chart-container">
                <VictoryChart
                    domainPadding={20}
                    containerComponent={
                        <VictoryVoronoiContainer
                            labels={({ datum }) => `Año: ${datum.year}\nOro: ${datum.gold}\nPlata: ${datum.silver}\nBronce: ${datum.bronze}`}
                            labelComponent={<VictoryTooltip cornerRadius={0} flyoutStyle={{ fill: "white" }} />}
                        />
                    }
                >
                    <VictoryAxis 
                     tickFormat={(x) => `${x}`}
                     tickValues={filteredData.map(d => d.year)}
                     />
                    <VictoryAxis dependentAxis />
                    <VictoryArea
                        data={filteredData}
                        x="year"
                        y={(datum) => datum.gold + datum.silver + datum.bronze}
                    />
                </VictoryChart>
            </div>
        </div>
    );
}

export default Achievements;
