import React, { useState } from "react";
import { VictoryArea, VictoryChart, VictoryAxis, VictoryStack } from 'victory';
import "./achievements.css";

const Achievements = () => {
    // Datos de ejemplo para los premios
    const allData = [
        { year: 2020, category: 'Ciencias', achievements: 5, name: "Premio A" },
        { year: 2021, category: 'Tecnología', achievements: 3, name: "Premio B" },
        // ... más datos ...
    ];

    const [selectedCategory, setSelectedCategory] = useState('Global');
    const [selectedYear, setSelectedYear] = useState('Global');
    const categories = ["Ciencias", "Tecnología", "Arte", "Otras", "Global"];
    const years = ['Global', ...Array.from({length: 19}, (_, i) => 2006 + i)];

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    const handleYearChange = (event) => {
        setSelectedYear(event.target.value);
    };

    const getFilteredData = (selectedCategory, selectedYear) => {
        if (selectedCategory === 'Global' || selectedYear === 'Global') {
            return allData;
        }
        return allData.filter(item => 
            item.category === selectedCategory && item.year.toString() === selectedYear
        );
    };

    const filteredData = getFilteredData(selectedCategory, selectedYear);

    return (
        <div className="achievements-container">
            <div className="achievements-banner">
                <h1>Logros y reconocimientos</h1>
                <p>Descubre nuestras destacadas contribuciones y logros.</p>
            </div>

            <div className="selection-controls">
                <select onChange={handleCategoryChange} value={selectedCategory}>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))}
                </select>

                <select onChange={handleYearChange} value={selectedYear}>
                    {years.map(year => (
                        <option key={year} value={year}>{year}</option>
                    ))}
                </select>
            </div>

            <div className="chart-container">
                <VictoryChart>
                    <VictoryAxis />
                    <VictoryAxis dependentAxis />
                    <VictoryStack>
                        <VictoryArea
                            data={filteredData}
                            x="year"
                            y="achievements"
                        />
                    </VictoryStack>
                </VictoryChart>
            </div>
        </div>
    );
}

export default Achievements;
