import React, { useState } from 'react';
import './Contact.css'; // Asegúrate de que la ruta al archivo CSS sea correcta

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes manejar la lógica de envío de datos del formulario
    console.log(formData);
  };

  return (
    <div className="contact-container">
      <div className="contact-info">
        <h2>¡COMUNÍCATE CON NOSOTROS!</h2>
        <p>O aún mejor, ¡Ven a visitarnos!</p>
        <p>Para más información, envíanos un correo a:</p>
        <p><a href="mailto:cientifico.alajuela@mep.go.cr">cientifico.alajuela@mep.go.cr</a></p>

        <div className="contact-address">
          <h3>COLEGIO CIENTÍFICO DE ALAJUELA</h3>
          <p>Avenida Central, Calle 18-20, Frente a Plaza Ferias</p>
          <p>Alajuela, Costa Rica</p>
          <p>Tel. (506) 2430-9782  (506) 2431-4405</p>
        </div>

        <div className="google-map">
          {/* Tu iframe de Google Maps aquí */}
        </div>
      </div>

      <div className="contact-form-container">
        <form onSubmit={handleSubmit} className="contact-form">
          <h2>Envíanos un Mensaje</h2>
          
          <label htmlFor="name">Nombre:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />

          <label htmlFor="email">Correo Electrónico:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />

          <label htmlFor="message">Mensaje:</label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
          ></textarea>

          <button type="submit">Enviar</button>
        </form>
      </div>
    </div>
  );
}

export default Contact;
