import React, { useState } from 'react';
import './Contact.css'; // Asegúrate de que la ruta al archivo CSS sea correcta
import FaFacebook from '../assets/'

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className="contact-page-container">
      <div className="contact-info-box">
        <h2>¡COMUNÍCATE CON NOSOTROS!</h2>
        <p>O aún mejor, ¡Ven a visitarnos!</p>
        <p>Para más información, envíanos un correo a:</p>
        <p><a href="mailto:cientifico.alajuela@mep.go.cr">cientifico.alajuela@mep.go.cr</a></p>

        <h3>COLEGIO CIENTÍFICO DE ALAJUELA</h3>
        <p>Avenida Central, Calle 18-20, Frente a Plaza Ferias</p>
        <p>Alajuela, Costa Rica</p>
        <p>Tel. (506) 2430-9782  (506) 2431-4405</p>

        <div className="social-media-links">
          <a href="https://www.facebook.com/YourFacebookPage" target="_blank" rel="noopener noreferrer">
            <FaFacebook className="social-icon" />
          </a>
          <a href="https://www.instagram.com/YourInstagram" target="_blank" rel="noopener noreferrer">
            <FaInstagram className="social-icon" />
          </a>
        </div>

        <div className="google-map-box">
          {/* Tu iframe de Google Maps aquí */}
        </div>
      </div>
      
      <div className="contact-form-box">
        {/* Formulario de contacto */}
      </div>
    </div>
  );
}

export default Contact;
