import React from 'react';
import './about_us.css';

import AdditionalImage1 from '../../assets/banners/Banner 1.jpg';
import AdditionalImage2 from '../../assets/banners/Banner 2.jpg';

import History from './history/history';
import Team from './team/team';

const AboutUs = () => {
  return (
    <div className="about-us-container">
      <div className="about-us-banner">
        <h1>Conócenos</h1>
        <p>Descubre más sobre nuestra institución.</p>
      </div>

      <div className="section flex-section">
        <div className="text-content">
          <h2>Nuestra Historia</h2>
          <History />
          <p>El Colegio Científico de Costa Rica en Alajuela, fundado en 2006, es una institución preuniversitaria destacada por su excelencia académica...</p>
        </div>
        <img src={AdditionalImage1} alt="Imagen Adicional 1" className="section-image" />
      </div>

      <div className="section flex-section reverse-flex">
        <div className="text-content">
          <h2>Nuestro Equipo</h2>
          <div className='Team-element'>
            /*<Team />
          </div>
          <p>Nuestro equipo está conformado por docentes con una destacada habilidad para la enseñanza...</p>
        </div>
        <img src={AdditionalImage2} alt="Imagen Adicional 2" className="section-image" />
      </div>

      <div className="white-section-alone">
        <h2>Título de la Sección Adicional</h2>
        <p>Esta sección adicional puede ser usada para proporcionar más información...</p>
      </div>
    </div>
  );
}

export default AboutUs;
