import React, { useState } from 'react';
import './Access.css'; // Asegúrate de que este archivo contenga los nuevos estilos CSS
import GoogleLogo from '../assets/Google__G__logo.svg';
const Access = () => {
  const [userData, setUserData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica para manejar el envío de datos
  };

  return (
    <div className="container-registro">
      <form className="formulario-registro">
        <div className="columna-flexible">
          <label className="etiqueta">Email</label>
          <div className="contenedor-input">
            {/* Icono SVG para Email */}
            {/* Incluye aquí el SVG correspondiente */}
            <input
              type="email"
              className="campo-input"
              placeholder="Enter your Email"
              name="email"
              value={userData.email}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="columna-flexible">
          <label className="etiqueta">Password</label>
          <div className="contenedor-input">
            {/* Icono SVG para Password */}
            {/* Incluye aquí el SVG correspondiente */}
            <input
              type="password"
              className="campo-input"
              placeholder="Enter your Password"
              name="password"
              value={userData.password}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="fila-flexible">
          <div className="contenedor-checkbox">
            <input type="checkbox" className="checkbox" />
            <label className="etiqueta">Remember me</label>
          </div>
          <span className="texto-enlace">Forgot password?</span>
        </div>

        <button className="boton-enviar">Sign In</button>

        <p className="p">
          Don't have an account? <span className="texto-enlace">Sign Up</span>
        </p>
        <p className="p line">Or With</p>

        <div className="fila-flexibles">
          <button className="boton-social google">
            <img src={GoogleLogo} alt="Google_Logo"/>
          </button>
        </div>
      </form>
    </div>
  );
};

export default Access;
