import React, { useState } from "react";
import "./achievements.css";
import Trophy from '../../assets/plus/trofe.png';
import Excelence from '../../assets/plus/Excelencia.png';

// Componente de sección de acordeón
const AccordionSection = ({ title, children }) => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div className="accordion-section">
            <button className="accordion-title" onClick={() => setIsOpen(!isOpen)}>
                {title}
            </button>
            {isOpen && <div className="accordion-content">{children}</div>}
        </div>
    );
};

const Achievements = () => {
    return (
        <div className="achievements-container">
            <div className="achievements-banner">
                <h1>Logros y reconocimientos</h1>
                <p>Descubre nuestras destacadas contribuciones y logros.</p>
            </div>

            <div className="section white-section">
                <AccordionSection title="Éxito Académico">
                    {/* Aquí inserta tu contenido detallado */}
                </AccordionSection>
                <AccordionSection title="Participación en Olimpiadas">
                    {/* Contenido detallado */}
                </AccordionSection>
                {/* Más secciones de acordeón según sea necesario */}
            </div>

            <div className="section photo-section">
                <div className="photo-section-text">
                    <p>Algunas de nuestras representaciones...</p>
                </div>
            </div>

            <div className="section white-section">
                <div className="two-column-section">
                    <img src={Excelence} alt="Excelencia" className="excelence-photo"/>
                    <div className="text-column">
                        <p>Nuestro rendimiento...</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Achievements;
