import React, { useState, useEffect } from 'react';
import './Home.css';
import heroImage from './Banners/Banner1.jpg'; // Ajusta la ruta de tu imagen

const Home = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [showAdditionalContent, setShowAdditionalContent] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const triggerSection = document.getElementById('features'); // Ajusta el ID según sea necesario
      const triggerPosition = triggerSection.offsetTop - window.innerHeight;

      if (window.scrollY > triggerPosition) {
        setShowPopup(true);
      } else {
        setShowPopup(false);
      }

      // Mostrar la caja adicional
      const additionalContent = document.querySelector('.additional-content');
      const additionalContentPosition = additionalContent.offsetTop - window.innerHeight;

      if (window.scrollY > additionalContentPosition) {
        setShowAdditionalContent(true);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const closePopup = () => {
    setShowPopup(false);
  };

  return (
    <>
      <div className="home-container">
        <div className="hero-image">
          <img src={heroImage} alt="Hero" />
        </div>
        <header className="hero-section">
          <h1 className="hero-title">Bienvenido a CCAL</h1>
          <p className="hero-description">Somos más que una fórmula.</p>
          <button className="cta-button" onClick={closePopup}>
            ¡Empezar Ahora!
          </button>
        </header>
      </div>

      {/* Contenido adicional debajo del botón */}
      <div className={`additional-content ${showAdditionalContent ? 'visible' : ''}`}>
        <h2>Información Adicional</h2>
        <p>
          Aquí puedes agregar más información sobre la universidad.
          Puedes incluir texto, imágenes y otros elementos según tus necesidades.
        </p>
      </div>

      {/* Sección de características */}
      <div id="features" className="features-section">
        {/* Agrega tus elementos de diseño aquí */}
        <div className="feature">
          <h2>Título de la característica</h2>
          <p>Descripción de la característica.</p>
        </div>
        {/* Agrega más elementos de diseño según sea necesario */}
      </div>

      {/* Caja emergente */}
      {showPopup && (
        <div className="popup">
          <span className="close" onClick={closePopup}>&times;</span>
          <h2>Caja Emergente</h2>
          <p>Contenido de la caja emergente.</p>
        </div>
      )}
    </>
  );
};

export default Home;
