// Navbar.js

import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import './Navbar.css';
import LogoSvg from '../Logo.svg';

const Navbar = () => {
  const location = useLocation();

  // Verifica si la ruta actual es '/acceder'
  const isAccessPage = location.pathname === '/acceder';

  return (
    // Muestra el Navbar solo si no estamos en la página de acceso
    !isAccessPage && (
      <nav className="navbar">
        <div className="logo-container">
          <NavLink to="/">
            <img src={LogoSvg} alt="CCAL Logo" className="logo" />
          </NavLink>
        </div>

        <div className="nav-links-container">
          <ul className="nav-links">
            <li className="nav-item">
              <NavLink to="/" exact className="nav-link" activeClassName="active-link">
                Inicio
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink to="/conocenos" className="nav-link" activeClassName="active-link">
                Conócenos
              </NavLink>
            </li>
          </ul>
        </div>
      </nav>
    )
  );
};

export default Navbar;
