import React from 'react';
import './ome.css';

const Home = () => {
  return (
    <div className="home-container">
      <header className="hero-section">
        <h1 className="hero-title">Bienvenido a Netnook</h1>
        <p className="hero-description">
          Descubre, guarda y comparte tus contenidos favoritos de manera fácil y rápida.
        </p>
        <button className="cta-button">¡Empezar Ahora!</button>
      </header>

      <section className="features-section">
        <Feature title="Explora Contenidos">
          <p>Accede a una amplia variedad de contenidos en un solo lugar.</p>
        </Feature>
        <Feature title="Guarda tus Favoritos">
          <p>Guarda tus contenidos preferidos para acceder a ellos cuando quieras.</p>
        </Feature>
        <Feature title="Comparte con Amigos">
          <p>Comparte tus descubrimientos con amigos y sigue sus recomendaciones.</p>
        </Feature>
      </section>
    </div>
  );
};

// Componente reutilizable para las características
const Feature = ({ title, children }) => {
  return (
    <div className="feature">
      <h2>{title}</h2>
      {children}
    </div>
  );
};

export default Home;
