import React from 'react';
import './About-us.css';
import heroImage from './Banners/Banner 2.jpg';

const AboutUs = () => {
  return (
    <div className="aboutUs-container">

      {/* Sección del Banner Principal */}
      <div className="hero-section">
        
        <img className="hero-image" src={heroImage} alt="Hero" />
        <header>
          <h1>Bienvenido a CCAL</h1>
          <p>Somos más que una formula.</p>
          <button>¡Empezar Ahora!</button>
        </header>
      </div>

      {/* Sección de Bienvenida */}
      <section className="bienvenida-section">
        <h2>Bienvenida a Nuestra Institución</h2>
        <p>Texto introductorio sobre la institución y su misión.</p>
      </section>

      {/* Sección de Historia del Colegio */}
      <section className="historia-section">
        <h2>Nuestra Historia</h2>
        <p>Una breve reseña de los orígenes y hitos importantes del colegio.</p>
      </section>

      {/* Otras secciones... */}

      {/* Pie de Página */}
      <footer>
        <p>Contacto: info@ccal.edu</p>
        {/* Otros elementos del pie de página */}
      </footer>

    </div>
  );
};

export default AboutUs;
